import { Component } from '@angular/core';

@Component({
  selector: 'app-tabla',
  templateUrl: './tabla.component.html',
  styleUrls: ['./tabla.component.css']
})
export class TablaComponent {

  nombre = "Juan Paz";
  edad=23;

  peliculas=[
    {
      titulo: "Matrix",
      genero: "accion",
      anio: "1999"
    },
    {
      titulo: "Titanic",
      genero: "Romance",
      anio: "2002"
    },
    {
      titulo: "Frozen",
      genero: "Animada",
      anio: "2019"
    },
    {
      titulo: "Jumanji",
      genero: "Ciencia ficción",
      anio: "2021"
    }
  ]

}
